import {
    SEARCH_START,
    SEARCH_SUCCESS,
    SEARCH_FAILURE
} from 'actionTypes'


export const search = word => async dispatch => {
    dispatch({type: SEARCH_START})

    try {
        dispatch({
            type: SEARCH_SUCCESS,
            payload: word
        })
    } catch (err) {
        dispatch({
            type: SEARCH_FAILURE,
            payload: err,
            error: true
        })
    }
}