import React, {Component} from 'react'
import Fade from 'react-reveal/Fade'
import {connect} from 'react-redux'

import {getResult} from 'selectors'







class Result extends Component {

    showResult() {
        if (this.props.result[0] === undefined ) return <img src='img/loader.svg' alt=""/>
        else {
            return this.props.result.map(track => (
                <div key={track.name} className='mt-4 ml-5 p-2 hvr-sweep-to-right custom-card ' style={{cursor: 'pointer'}}><img src={track.image[2]["#text"]} className='p-3' alt=""/>{track.artist} - {track.name}</div>
            ))
        }


    }


    render() {

        return (
            <div>

                <Fade top opposite cascade collapse>
                    <ul className='mt-5'>
                        <li className="row">
                            <div className="col">
                                {this.showResult()}

                            </div>
                        </li>
                    </ul>
                </Fade>
            </div>
        );
    }
}


const mapStateToProps = state => ({
    result: getResult(state)
})

export default connect(mapStateToProps, null)(Result)
